// ── Typewriter ────────────────────────────────────────────────────────────────
const Typewriter = ({ text, speed = 32, onDone }) => {
  const [displayed, setDisplayed] = useState("");
  const [done, setDone] = useState(false);
  const idx = useRef(0);
  const tRef = useRef(null);

  useEffect(() => {
    setDisplayed(""); setDone(false); idx.current = 0;
    const tick = () => {
      if (idx.current < text.length) {
        idx.current++;
        setDisplayed(text.slice(0, idx.current));
        tRef.current = setTimeout(tick, speed);
      } else { setDone(true); onDone && onDone(); }
    };
    tRef.current = setTimeout(tick, 60);
    return () => clearTimeout(tRef.current);
  }, [text]);

  return (
    <p style={{ color:"rgba(255,255,255,0.96)", fontSize:"15px", lineHeight:1.75,
      margin:0, fontStyle:"italic", minHeight:"3.4em" }}>
      {displayed}
      {!done && <span style={{ opacity:0.6, animation:"blink 0.8s step-end infinite" }}>|</span>}
    </p>
  );
};

