// ── Character Speech Panel ────────────────────────────────────────────────────
const CharacterSpeech = ({
  princess, text, mood = "happy",
  onDone, choices, onChoice,
  showSkip, onSkip,
}) => {
  const [typeDone, setTypeDone] = useState(false);
  const [choiceHover, setChoiceHover] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const bubbleRef = useRef(null);
  const voiceTimerRef = useRef(null);

  useEffect(() => {
    setTypeDone(false);
    setIsPlaying(false);

    if (bubbleRef.current) {
      gsap.fromTo(bubbleRef.current,
        { y: 18, opacity: 0, scale: 0.94 },
        { y: 0, opacity: 1, scale: 1, duration: 0.42, ease: "back.out(1.8)" }
      );
    }

    // Speak the line — fire and forget, story never waits for this
    const t = setTimeout(() => {
      setIsPlaying(true);
      speakLine(text, () => setIsPlaying(false));
    }, 300);

    return () => clearTimeout(t);
    // Note: we do NOT stopSpeech in cleanup — that would kill the new line's voice
  }, [text]);

  const toggleVoice = () => {
    if (isPlaying) {
      stopSpeech();
      setIsPlaying(false);
    } else {
      setIsPlaying(true);
      speakLine(text, () => setIsPlaying(false));
    }
  };

  const handleChoice = (c) => {
    stopSpeech();
    setIsPlaying(false);
    playChime(580 + c.id * 80);
    onChoice && onChoice(c);
  };

  return (
    <div ref={bubbleRef} style={{
      display: "flex", alignItems: "flex-end", gap: "16px",
      background: "rgba(4,4,28,0.84)", backdropFilter: "blur(22px)",
      border: `2px solid ${princess.color}55`,
      borderRadius: "24px", padding: "18px 20px",
      boxShadow: `0 10px 50px rgba(0,0,0,0.65), 0 0 40px ${princess.color}18`,
    }}>
      {/* Floating PNG portrait */}
      <div style={{ display: "flex", flexDirection: "column", alignItems: "center", gap: "6px" }}>
        <CharacterPortrait princess={princess} mood={mood} size={110} />
        <span style={{
          color: princess.color, fontSize: "10px", fontWeight: 800,
          letterSpacing: "0.16em", textTransform: "uppercase",
          background: "rgba(0,0,0,0.52)", borderRadius: "6px", padding: "2px 9px",
          border: `1px solid ${princess.color}44`, whiteSpace: "nowrap",
        }}>{princess.name}</span>
      </div>

      {/* Dialogue area */}
      <div style={{ flex: 1, display: "flex", flexDirection: "column", gap: "11px" }}>
        {/* Text box + voice toggle */}
        <div style={{
          background: "rgba(255,255,255,0.04)", borderRadius: "16px",
          padding: "14px 16px", border: `1px solid ${princess.color}22`,
          position: "relative",
        }}>
          {/* 🔊 Voice button — top right corner */}
          <button
            onClick={toggleVoice}
            title={isPlaying ? "Stop voice" : "Play voice"}
            style={{
              position: "absolute", top: "10px", right: "12px",
              width: "34px", height: "34px", borderRadius: "50%", flexShrink: 0,
              background: isPlaying ? `${princess.color}44` : `${princess.color}18`,
              border: `2px solid ${isPlaying ? princess.color : princess.color + "44"}`,
              cursor: "pointer", fontSize: "15px",
              display: "flex", alignItems: "center", justifyContent: "center",
              transition: "all 0.2s",
              boxShadow: isPlaying ? `0 0 14px ${princess.color}88` : "none",
              animation: isPlaying ? "voicePulse 1.2s ease-in-out infinite" : "none",
            }}>
            {isPlaying ? "⏹" : "🔊"}
          </button>

          <div style={{ paddingRight: "44px" }}>
            <Typewriter
              text={text}
              // onDone fires from typewriter — completely independent of voice
              onDone={() => setTypeDone(true)}
            />
          </div>
        </div>

        {/* Choice buttons — appear as soon as typing finishes */}
        {typeDone && choices && choices.length > 0 && (
          <div style={{ display: "flex", flexDirection: "column", gap: "8px" }}>
            {choices.map(c => (
              <button key={c.id} onClick={() => handleChoice(c)}
                onMouseEnter={() => setChoiceHover(c.id)}
                onMouseLeave={() => setChoiceHover(null)}
                style={{
                  padding: "10px 16px",
                  background: choiceHover === c.id ? `${princess.color}33` : `${princess.color}14`,
                  border: `1.5px solid ${choiceHover === c.id ? princess.color : princess.color + "44"}`,
                  borderRadius: "12px", color: "#fff",
                  fontSize: "13px", fontWeight: 700,
                  cursor: "pointer", textAlign: "left",
                  transition: "all 0.18s",
                  transform: choiceHover === c.id ? "translateX(5px)" : "translateX(0)",
                  boxShadow: choiceHover === c.id ? `0 4px 20px ${princess.color}44` : "none",
                }}>
                <span style={{ marginRight: "8px", fontSize: "16px" }}>{c.emoji}</span>
                {c.text}
              </button>
            ))}
          </div>
        )}

        {/* Continue / skip */}
        {typeDone && showSkip && (
          <button onClick={() => { stopSpeech(); setIsPlaying(false); onSkip(); }} style={{
            alignSelf: "flex-end", background: "none", border: "none",
            color: "rgba(255,255,255,0.38)", fontSize: "12px",
            cursor: "pointer", textDecoration: "underline",
          }}>Continue ▶</button>
        )}
      </div>
    </div>
  );
};

